  /* Mobile Touch Optimizations */

/* Prevent touch callouts and text selection on interactive elements */

.touch-optimized {
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
}

/* Improve touch targets for mobile */

@media (max-width: 768px) {
  /* Larger touch targets for buttons */
  button:not(.btn-compact) {
    min-height: 44px !important;
    min-width: 44px !important;
    padding: 0.5rem 1rem !important;
    touch-action: manipulation;
  }

  /* Larger touch targets for select triggers */
  [data-radix-select-trigger] {
    min-height: 44px !important;
    touch-action: manipulation;
  }

  /* Larger touch targets for tabs */
  [data-radix-tabs-trigger] {
    min-height: 44px !important;
    padding: 0.5rem 1rem !important;
    touch-action: manipulation;
  }

  /* Prevent zoom on input focus */
  input[type="text"],
  input[type="search"],
  input[type="email"],
  input[type="password"],
  textarea,
  select {
    font-size: 16px !important;
    touch-action: manipulation;
  }

  /* Improve scroll behavior for popovers and dropdowns */
  [data-radix-popover-content],
  [data-radix-dropdown-menu-content] {
    overscroll-behavior: contain;
    -webkit-overflow-scrolling: touch;
  }

  /* Prevent popovers from closing on scroll inside content */
  [data-radix-popover-content] {
    touch-action: pan-y;
  }

  /* Better spacing for mobile resource cards */
  .mobile-resource-card {
    min-height: 60px;
    padding: 1rem;
    touch-action: manipulation;
  }

  /* Improve mobile layout for control buttons */
  .mobile-controls {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
    width: 100%;
  }

  .mobile-controls > div {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
    align-items: center;
  }

  /* Better mobile navigation */
  .mobile-nav-button {
    min-height: 48px;
    min-width: 48px;
    touch-action: manipulation;
    -webkit-touch-callout: none;
  }

  /* Improve popover positioning on mobile */
  [data-radix-popover-content] {
    max-width: calc(100vw - 2rem);
    max-height: 70vh;
    overflow-y: auto;
  }

  /* Better dialog sizing on mobile - but not sheets */
  [data-radix-dialog-content]:not([data-side]) {
    max-width: calc(100vw - 1rem);
    max-height: 95vh;
    width: 95vw;
    margin: auto;
    overflow-y: auto;
    position: fixed;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
  }

  /* Ensure mobile-optimized dialogs are properly centered */
  [data-radix-dialog-content][data-mobile-optimized="true"] {
    left: 50% !important;
    right: auto !important;
    transform: translate(-50%, -50%) !important;
    max-width: min(95vw, 28rem) !important;
  }

  /* Fix for all dialogs on mobile to prevent off-screen rendering - but not sheets */
  [data-radix-dialog-content]:not([data-side]) {
    --dialog-width: calc(100vw - 2rem);
    width: var(--dialog-width) !important;
    max-width: min(var(--dialog-width), 32rem) !important;
    left: 50% !important;
    transform: translateX(-50%) translateY(-50%) !important;
  }

  /* Exception for sheets - don't center them */
  [data-radix-dialog-content][data-side] {
    left: 0 !important;
    right: auto !important;
    top: 0 !important;
    bottom: 0 !important;
    transform: none !important;
    width: var(--sidebar-width, 18rem) !important;
    max-width: 85vw !important;
    padding-left: env(safe-area-inset-left);
  }

  /* Sidebar fixes for mobile */
  [data-sidebar] {
    z-index: 50;
  }

  /* Ensure content doesn't overflow on mobile */
  .grid {
    width: 100%;
    overflow-x: hidden;
  }

  /* Resource cards should be touch-friendly */
  [data-testid="resource-card"] {
    min-height: 60px;
    padding: 0.75rem;
    touch-action: manipulation;
  }

  /* Improve sheet behavior on mobile */
  [data-radix-dialog-content][data-state="open"] {
    animation-duration: 0.3s;
  }
}

/* Prevent text selection on buttons and interactive elements */

button,
[role="button"],
[data-radix-select-trigger],
[data-radix-dropdown-menu-trigger],
[data-radix-popover-trigger],
[data-radix-tabs-trigger] {
  -webkit-user-select: none;
  -moz-user-select: none;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
}

/* Better focus states for mobile */

@media (max-width: 768px) {
  *:focus-visible {
    outline: 2px solid hsl(var(--ring));
    outline-offset: 2px;
  }

  /* Remove hover states on mobile */
  @media (hover: none) {
    .hover\:bg-accent:hover {
      background-color: transparent;
    }

    .hover\:bg-accent\/50:hover {
      background-color: transparent;
    }
  }
}

/* Enhanced scroll lock for mobile */

body.scroll-lock,
html.scroll-lock {
  overflow: hidden !important;
  position: fixed !important;
  width: 100% !important;
  height: 100% !important;
  touch-action: none !important;
}

/* Maintain scroll position when lock is removed */

body.scroll-lock {
  top: var(--scroll-top, 0) !important;
}

/* Ensure sidebar overlay and content are above page content */

[data-radix-dialog-overlay] {
  z-index: 70 !important;
}

[data-radix-dialog-content][data-side] {
  z-index: 80 !important;
}

/* Mobile-specific popover improvements */

@media (max-width: 768px) {
  /* Make popovers full-width on very small screens */
  @media (max-width: 480px) {
    [data-radix-popover-content] {
      width: calc(100vw - 1rem);
      margin: 0.5rem;
      left: 0.5rem !important;
      right: 0.5rem !important;
      transform: none !important;
    }

    /* Fix dialog positioning on mobile */
    [data-radix-dialog-overlay] {
      position: fixed;
      inset: 0;
    }

    [data-radix-dialog-content]:not([data-side]) {
      position: fixed !important;
      left: 50% !important;
      top: 50% !important;
      transform: translate(-50%, -50%) !important;
      width: calc(100vw - 1rem) !important;
      max-width: 28rem !important;
      margin: 0 !important;
    }

    /* Keep sheets on the side even on very small screens */
    [data-radix-dialog-content][data-side] {
      position: fixed !important;
      left: 0 !important;
      top: 0 !important;
      transform: none !important;
      width: var(--sidebar-width, 18rem) !important;
      max-width: 80vw !important;
      height: 100% !important;
      margin: 0 !important;
    }
  }

  /* Improve touch scrolling */
  .scroll-area,
  [data-radix-scroll-area-viewport] {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: contain;
  }
}

/* Skeleton Loading Animations */

@keyframes shimmer {
  0% {
    background-position: -468px 0;
  }
  100% {
    background-position: 468px 0;
  }
}

/* Shimmer effect for skeleton cards - optimized for performance */

.animate-shimmer {
  animation: shimmer 1.5s ease-in-out infinite;
  background: linear-gradient(
    90deg,
    var(--skeleton-base) 0px,
    var(--skeleton-shine) 20px,
    var(--skeleton-base) 40px
  );
  background-size: 468px 100%;
  will-change: background-position;
  transform: translateZ(0); /* Hardware acceleration */
}

/* Skeleton color variables */

:root {
  --skeleton-base: hsl(var(--muted));
  --skeleton-shine: hsl(var(--muted) / 0.5);
}

.dark {
  --skeleton-base: hsl(var(--muted));
  --skeleton-shine: hsl(var(--muted) / 0.3);
}

/* Progressive fade-in animation - optimized */

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.fade-in {
  animation: fadeIn 0.3s ease-out forwards;
  will-change: opacity;
}

/* Fast fade for content switching */

@keyframes quickFade {
  from {
    opacity: 0;
    transform: scale(0.98);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.quick-fade {
  animation: quickFade 0.2s ease-out forwards;
  will-change: opacity, transform;
  transform: translateZ(0);
}

/* Content fade-in on load - no stagger to prevent jitter */

.content-fade-in {
  animation: fadeIn 0.2s ease-out forwards;
  will-change: opacity;
  transform: translateZ(0); /* Hardware acceleration */
}

/* Smooth transition for visibility changes */

.smooth-transition {
  transition: opacity 0.2s ease-out, transform 0.2s ease-out;
  will-change: opacity, transform;
}

/* Optimize mobile performance */

@media (max-width: 768px) {
  .content-fade-in {
    animation-duration: 0.15s;
  }

  .smooth-transition {
    transition-duration: 0.15s;
  }
}

/* Disable animations for reduced motion preference */

@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Subtle pulse animation for skeleton elements */

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

.skeleton-pulse {
  animation: pulse 1.5s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  will-change: opacity;
  transform: translateZ(0); /* Hardware acceleration */
}

/* Optimized loading transition */

.loading-transition-enter {
  opacity: 0;
  transform: scale(0.95);
}

.loading-transition-active {
  opacity: 1;
  transform: scale(1);
  transition: opacity 0.2s ease-out, transform 0.2s ease-out;
  will-change: opacity, transform;
}

/* Prevent layout shift during loading */

.skeleton-container {
  min-height: inherit;
  contain: layout;
}

/* Hardware accelerated transitions */

.hw-accelerated {
  transform: translateZ(0);
  backface-visibility: hidden;
  perspective: 1000px;
}

/* Fix for content disappearing on scroll - optimized */

html {
  scroll-behavior: smooth;
  overflow-x: hidden;
  /* Optimize scroll performance */
  scroll-padding-top: 80px;
}

body {
  overflow-x: hidden;
  min-height: 100vh;
  /* Prevent overscroll bounce */
  overscroll-behavior: none;
}

/* Prevent layout shifts during scroll */

* {
  box-sizing: border-box;
}

/* Improve scroll performance with passive events */

.scroll-container {
  scroll-behavior: smooth;
  /* Optimize for touch scrolling - iOS momentum scrolling */
  -webkit-overflow-scrolling: touch;
  /* Direct touch action for better performance */
  touch-action: pan-y;
  /* GPU acceleration - minimal usage */
  transform: translateZ(0);
  backface-visibility: hidden;
  /* Ensure scrollable */
  overflow-y: auto;
}

/* Fix card content overflow and cutoff issues */

.card-content-fix {
  contain: layout style;
  /* Minimal GPU usage - removed will-change */
  transform: translateZ(0);
}

/* Ensure card layouts don't clip content */

[data-radix-aspect-ratio-wrapper] {
  contain: layout;
  overflow: visible !important;
}

/* Optimize card rendering performance */

.card {
  /* GPU acceleration */
  transform: translateZ(0);
  backface-visibility: hidden;
  /* Removed will-change for better performance */
}

/* Fix button and interactive element positioning */

.flex-shrink-0 {
  flex-shrink: 0 !important;
}

/* Prevent content cutoff in card grids */

.grid {
  overflow: visible;
}

.grid > * {
  overflow: visible;
}

/* Remove problematic card overrides */

/* Improve badge and content wrapping */

.flex-wrap {
  flex-wrap: wrap;
  align-items: flex-start;
}

/* Ensure proper spacing in card layouts */

.card-spacing-fix {
  margin: 0.25rem;
  padding: 0.25rem;
}

/* Intersection observer optimization for lazy loading */

.lazy-load {
  opacity: 0;
  transition: opacity 0.3s ease-out;
}

.lazy-load-visible {
  opacity: 1;
}

/* Ensure main content area is properly positioned */

main {
  position: relative;
  z-index: 1;
  background-color: inherit;
  min-height: 100vh;
}

/* Fix for table visibility */

table {
  position: relative;
  z-index: 1;
  background-color: hsl(var(--card));
}

/* Ensure content containers are visible */

.space-y-6,
.space-y-4,
.space-y-2 {
  position: relative;
  z-index: 1;
}

/* Fix sticky header positioning */

header.sticky {
  z-index: 50 !important;
}

/* Ensure sidebar doesn't overlap content */

[data-sidebar] {
  z-index: 40;
}

/* Content area should be above background */

[data-sidebar-inset] {
  position: relative;
  z-index: 1;
  background-color: hsl(var(--background));
}

/* Table rows and cells visibility */

tr, td, th {
  background-color: transparent;
}

tbody tr:hover {
  background-color: hsl(var(--muted));
}

/* Ensure text is always visible */

p, span, div, h1, h2, h3, h4, h5, h6 {
  color: hsl(var(--foreground));
}

/* Fix for compact view items */

.compact-item {
  background-color: hsl(var(--card));
  color: hsl(var(--card-foreground));
}

/* Content containment improvements */

.contain-layout {
  contain: layout;
  /* Minimal hardware acceleration */
  transform: translateZ(0);
}

/* Grid container improvements with GPU optimization */

.grid {
  contain: layout;
  width: 100%;
  /* Minimal GPU usage */
  transform: translateZ(0);
  backface-visibility: hidden;
}

/* Mobile-specific scroll optimizations */

@media (max-width: 768px) {
  /* Optimize mobile scrolling */
  .scroll-container {
    /* Enable momentum scrolling for scrollable containers */
    -webkit-overflow-scrolling: touch;
    /* Ensure proper touch handling */
    touch-action: pan-y;
  }

  .grid,
  .card {
    /* Remove will-change for better performance */
    will-change: auto;
  }

  /* Prevent scroll jank on mobile */
  * {
    -webkit-tap-highlight-color: transparent;
    -webkit-touch-callout: none;
  }

  /* Optimize touch scrolling */
  .scroll-container {
    touch-action: pan-y;
  }
}

/* Ensure all cards maintain proper boundaries */

.group {
  contain: layout;
}

/* Text content overflow handling */

.line-clamp-1 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1;
  word-break: break-word;
}

.line-clamp-2 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 2;
  word-break: break-word;
}

.line-clamp-3 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3;
  word-break: break-word;
}

/* Badge text truncation */

.badge-text {
  max-width: 120px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

